def exists(x):
    t = type(x)
    if t == str:
        return x.strip() != ""
    if t == list:
        return len(x) > 0
    if t == dict:
        return len(x.keys()) > 0
    return x != None

def test(r, s, flags=0):
    if not isString(s):
        return
    return bool(re.search(r, s, flags))

def isPublicFile(x):
    if isIgnoredFile(x):
        return 0
    return test("^[a-zA-Z]|^\d+$", tail(x))

def isNumber(x):
    if isString(x):
        return test("^\d[.,\d]*$", x)
    return type(x) == int or type(x) == float

def head(f):
    return (
        re.sub("/$", "", str(f)).rsplit("/", maxsplit=1)[0]
        + "/"
    )

def tail(x):
    return re.sub("/$", "", str(x)).rsplit("/", maxsplit=1)[
        -1
    ]

def getExtension(s):
    if "json.js" in s:
        return "json.js"
    return search("\.([a-zA-Z]+\d*)$", s).lower()

def unique(a, b=None):
    if b:
        if isArray(a):
            return difference(a, b)
        if isObject(a):
            return filter(a, lambda k, v: k not in b)
    else:
        return list(set(a))

def toArray(x):
    if isArray(x):
        return x
    if isString(x):
        return split(x, "\n")
    return [x]

def every(x, y):
    if isArray(y):
        a = list(x).sort()
        b = list(y).sort()
        return json.dumps(a) == json.dumps(b)

    for item in list(x):
        if not y(item):
            return False
    return True

def datestamp(x=None, strife="%m-%d-%Y"):
    if hasattr(x, 'created_utc'):
        x = x.created_utc
        strife = 'praw'
    elif isObject(x)  and x.get('created_utc'):
        x = x['created_utc']
        strife = 'praw'

    ref = {
        "/": "%m/%d/%Y",
        "-": "%m-%d-%Y",
        "human": "%A %B %d, %-I:%M:%S%p",
        "praw": "%m-%d-%Y %-I:%M:%S%p",
        'long': "%A %B %d %Y, %H:%M:%S%p",
        'long': "%m-%d-%Y, %H:%M:%S%p",
    }
    strife = ref.get(strife, strife)
    if isString(x):
        if isfile(x):
            x = mdate(x)
            return datetime.fromtimestamp(x).strftime(
                strife
            )
        return datetime.strptime(x, strife)
    if type(x) == float or type(x) == int:
        return datetime.fromtimestamp(x).strftime(strife)
    if type(x) == datetime:
        return x.strftime(strife)

    return datetime.now().strftime(strife)

def search(regex, item, flags=0):
    match = re.search(regex, item, flags)
    if not match:
        return ""
    if match.groups():
        if len(match.groups()) == 1:
            return match.groups()[0]
        return match.groups()
    return match.group(0)

def clear(x):
    if isfile(x):
        with open(x, "w") as f:
            pass

    elif isdir(x):
        rmdir(x, None, True)

def absdir(dir="."):
    if isArray(dir):
        return dir
    dir = abspath(dir)
    return [os.path.join(dir, f) for f in os.listdir(dir)]

def getCaller(n=0):
    from inspect import stack

    stack = stack()
    if n == -1:
        return stack[2][3]
    else:
        return stack[len(stack) - 2][3]

def filter(items, f=exists, *args, **kwargs):
    if not f:
        return items
    if isString(f):
        ref = {
            'camel_case': '[a-z][A-Z]'
        }
        r = f
        if r in ref:
            r = ref[r]
        f = lambda s: test(r, s)
    if isObject(items):
        return {k: v for k, v in items.items() if f(k, v)}
    else:
        if isArray(f):
            ignore = f
            f = lambda x: x not in ignore

        if args and isFunction(args[0]):
            return [
                x for x in list(items) if f(x, **kwargs) and every(args, lambda check: check(x))
            ]

        return [
            x for x in list(items) if f(x, *args, **kwargs)
        ]

def isdir(f):
    return isString(f) and os.path.isdir(f)
    return os.path.isdir(os.path.expanduser(f))

def isRecent(file, before=0, after=0, **kwargs):
    if isNumber(file):
        n = int(file)
    elif os.path.exists(file):
        n = mdate(file)
    else:
        return False

    if before:
        return n < dategetter(before)
    if after:
        return n > dategetter(after)

    seconds = toSeconds(**kwargs)
    currentTime = timestamp()
    lastAcceptableTime = currentTime - seconds
    if n > lastAcceptableTime:
        return True

def timestamp(x=int):
    if type(x) == datetime:
        return int(x.timestamp())
    if x == int:
        return int(datetime.now().timestamp())

def npath(dir=0, file=0):
    if not dir:
        return file
    elif not file:
        return os.path.join(os.getcwd(), dir)
    if isfile(dir):
        if not getExtension(file):
            file = addExtension(file, getExtension(dir))
        dir = head(dir)
    return os.path.join(dir, tail(file))

def normpath(dir, file):
    dir = abspath(dir) if isdir(dir) else head(dir)
    return os.path.join(dir, tail(file))

def abspath(file=None):
    if file == None:
        return os.path.expanduser("~/")
    elif file == ".":
        return os.getcwd()
    elif file.startswith("~"):
        return os.path.expanduser(file)
    elif file.startswith("./"):
        return os.path.join(os.getcwd(), file[2:])
    else:
        return os.path.abspath(file)

