def mfile(f, t, mode="move"):
    assert isfile(f)
    if t in env.dirdict:
        t = env.dirdict[t]

    if not getExtension(t) and not isdir(t):
        a = prompt('no extension for', t, 'did you forget it?')
        if a:
            t = addExtension(t, getExtension(f))
        else:
            prompt('are you sure you want to make a directory?', t)
            mkdir(t)
    elif not getExtension(t):
        t = normpath(t, f)

    if tail(f) == tail(t):
        print(mode, "file", tail(f), "to", head(t))
    else:
        print(f"{mode} file: {f} to {t}")

    if mode == "move":
        try:
            shutil.move(f, t)
            return 1
        except Exception as e:
            return 0
            pass

    elif mode == "copy":
        try:
            shutil.copy(f, t)
            return 1
        except Exception as e:
            return 0
            pass

