def find(arr, fn, mode=None, flags=0):
    fn = testf(fn)

    if isObject(arr):
        for k, v in arr.items():
            if fn in v:
                return os.path.join(k, fn)
        return

    for i, item in enumerate(list(arr)):
        if ftest(fn, item, flags=flags):
            return i if mode == int else item

