def sort(x, f=int, reverse=0):
    if isObject(x):
        return {
            k: v
            for k, v in sorted(
                x.items(),
                key=lambda item: f(item[1]),
                reverse=reverse,
            )
        }
    else:
        if isString(f):
            prev = f
            f = lambda x: x.get(prev)
        return sorted(list(x), key=f, reverse=reverse)

