def gatherArgs(args):
    if isArray(args[0]):
        return args[0]
    return args

def join(*args, delimiter="\n"):
    if not args:
        return ""

    if not args[0]:
        return ""

    s = ""
    for item in gatherArgs(args):
        if isArray(item):
            item = join(item)
        s += item
        s += "\n\n" if "\n" in item else delimiter

    return backspace(s) if delimiter else s

