def ftest(f, arg, flags=0):
    if isArray(f):
        return every(f, lambda f: ftest(f, arg))
    if isString(f):
        return test(f, arg, flags=flags)
    if isFunction(f):
        return f(arg)
    return True

def rigidSort(items, order, f=identity):
    order = {k: i for k in order}
    items.sort(key=lambda x: order[f(x)])
    return items

