def _write(f, s, _open=0):
    if not exists(s):
        return
    e = getExtension(f)

    if e == "recent":
        with open(f, "w") as _f:
            if isString(s):
                _f.write(s)
            else:
                json.dump(s, _f, indent=2)

    elif e == "json":
        with open(f, "w") as _f:
            json.dump(s, _f, indent=2)
    else:
        with open(f, "w") as _f:
            _f.write(toString(s))

    print(f"writing file: {f}")
    if _open:
        ofile(f)

def uncapitalize(s):
    return re.sub(
        "[a-zA-Z]", lambda x: x.group(0).lower(), s, count=1
    )

def stringify(x):
    if type(x) == bytes:
        return x.decode()
    if isPrimitive(x):
        return str(x)
    return json.dumps(x, indent=4)

def removeComments(s, e=None):
    raise Exception()
    js = "(?://|/\*[\w\W]+?\*/)"
    html = "<!--[\w\W]*?-->"
    start = "^ *"
    end = ".*\n+"

    if not e:
        if test(html, s):
            e = "html"

        elif test(js, s):
            e = "js"

    r = start + locals().get(e, "js") + end
    return re.sub(r, "", s, flags=re.M)

def createVariable(name, s, lang="py"):
    if test("^(fun|def|class)", s):
        return s
    prefix = "const " if lang == "js" else ""
    if not name:
        name = "PLACE_HOLDER"
    if isString(s) and not isJsonParsable(s):
        s = quote(s)
    s = stringify(s)
    return prefix + name + " = " + s

def unidecode(s):
