def templater(template, ref):

    template = template.strip()
    if isPrimitive(ref):
        ref = [str(ref)]
    regex = "\$(\w+)"

    def parser(x):
        if isArray(ref):
            return ref[int(x) - 1]

        if isObject(ref):
            return ref.get(x, fallback(x))

        if isFunction(ref):
            return ref(x)

    def runner(x):
        return parser(x.group(1))

    return re.sub(regex, runner, template)

def map(items, fn, *args, filter=1, **kwargs):
    if not items:
        return []
    if isString(fn):
        _key = fn
        if isObject(items[0]):
            fn = lambda x: x.get(_key)
        else:
            fn = lambda x: search(_key, x)
    if isNestedArray(items):
        return [fn(a, b) for a, b in items]

    if isObject(items):
        store = {}
        for k, v in items.items():
            value = fn(k, v)
            if value:
                store[k] = value
            elif filter:
                continue
            else:
                store[k] = v
        return store

    store = []
    for item in toArray(items):
        try:
            value = fn(item, *args, **kwargs)
            if not (filter and not value):
                store.append(value)
        except Exception as e:
            prompt(item, error='ERROR AT MAP', message=e)
            continue
    return store

