def mget(r, s, flags=0, mode=dict):
    store = []

    def parser(x):
        if x.groups():
            store.append(
                x.group(1)
                if len(x.groups()) == 1
                else x.groups()
            )
        else:
            store.append(x.group(0))

        return ""

    s = re.sub(r, parser, s, flags=flags).strip()

    if mode == list:
        return [s.strip(), store]
    if mode == str:
        if not store:
            config = ""
        else:
            config = store[0]
        return [s, config]

    if not store:
        config = {}
    elif isNestedArray(store):
        config = {k: v for k, v in store}
    else:
        config = {k: True for k in store}

    return s, config

