def rangeFromString(s, offset=1):
    if isArray(s):
        return s

    def f(s):
        if "-" in s:
            a, b = split(s, " *- *")
            store = []
            for i in range(int(a), int(b) + 1):
                store.append(i - offset)
            return store
        elif s == "x":
            return None
        else:
            return [int(s) - offset]

    return flat(map(split(s, ", *| +(?=\w)"), f, filter=0))

