    return re.sub(r, g, s, **kwargs)

def memoize(fn):
  store = {}
  def wrapper(*args):
    if args in store:
      return store[args]
    else:
      value = fn(*args)
      store[args] = value
      return value
  return wrapper

def dollarPrompt(x):
    if isArray(x):

        os.system("clear")
        number(x)
        s = input('choose 1-based indexed\n')
        a, b = splitonce(s, ' ')
        item = x[int(a) - 1]
    elif isObject(x):
        item = choose(list(x.values()), mode=1)
    else:
        item = x

    @memoize
    def f(x):
        return prompt(item=item, fallback=x) or x

    @memoize
    def g(x):
        return prompt(item, 'input:')

    r1 = '\$([a-zA-Z]\w*)'
    r2 = '\$(\d+)'
    n = 0
    r0 = '\$1'
    if b and test(r0, item):
        item = sub(item, r0, b)
        n += 1

    while True:
        if test(r1, item):
            item = sub(item, r1, f, count=1)
        else:
            break

    while True:
        n += 1
        r = '\$' + str(n)
        if test(r, item):
            item = sub(item, r, g)
        else:
            break

    return item

