def checkpoint_factory(schema):

    def c2(**kwargs):
        #print(kwargs)
        def transform(a, b):
            #print(a, b)
            if b == str:
                return lambda x: test(a, x, flags=re.I)

            if b == bool:
                print(a, b)
                return lambda x: x == a

            if b == int:
                return lambda x: x > a

        store = []
        for k,v in schema.items():
            arg = kwargs.get(k)
            if arg != None:
                store.append([k, transform(arg, v)])

        def checkpoint(item):
            for k,v in store:
                arg = item.get(k)
                if arg and not v(arg):
                    return False

            return True
        return checkpoint

    return  c2

