def detect_content_type(text):
    # Check for HTML tags
    if '<html' in text or '<body' in text or '<div' in text:
        return 'HTML'

    code_keywords = ['const', 'function', 'def']
    #code_keywords = ['def', 'class', 'import', 'return', 'for', 'while', 'if', 'else', 'func]
    for keyword in code_keywords:
        if keyword in text:
            return 'Code'

    return 'Prose'

