def hasValue(x = None):
    if x == None or x == False:
        return False
    return True

def fparse(fn, *args):
    if not fn:
        return args[0]
    elif isFunction(fn):
        value = fn(*args)
        if hasValue(value):
            return value
        return args[0]
    else:
        return fn[0]

def dictf(ref, fallback = None):
    def runner(item):
        return ref.get(item, fparse(fallback, item)) or item
    return runner

def lines(s, ref=0):
    s = s.strip()
    lines = map(s.split("\n"), trim)
    if test("^ *\w+:", s, flags=re.M):
        lines = map(lines, lambda x: splitonce(x, " *: *"))

    if ref:
        f = dictf(ref)
        lines = [f(*el) for el in lines]

    return lines

def count_params(f):
    data = inspect.signature(f)
    return len(data.parameters)

def dprompt(*variables, **kwargs):

    strings = []
    caller = getCaller()
    store = OrderedDict()
    store["caller"] = caller

    for v in variables:
        try:
            vars = (
                inspect.currentframe().f_back.f_locals.items()
            )
            name = [
                v_name
                for v_name, v_val in vars
                if v_val is v
            ][0]
            store[name] = v
        except Exception as e:
            strings.append(v)

    for a, b in kwargs.items():
        store[a] = b

    if strings:
        store["strings"] = strings

    pprint(store)
    return input("")

def gitNames(dir):
    s = SystemCommand("git status --short", dir=dir).success
    pairs = unique(re.findall("(\S+) (\w+(?:\.\w+)+)", s))
    store = [[], []]
    for a, b in pairs:
        if a == "M":
            store[0].append(b)
        else:
            store[1].append(b)
    a, b = store
    return {
        "modified": a,
        "created": b,
    }

