def chalkf(color, mode=0):

    reset = "\033[0m"
    red = "\033[31m"
    green = "\033[32m"
    yellow = "\033[33m"
    blue = "\033[34m"
    purple = "\033[35m"
    cyan = "\033[36m"
    bold = "\033[1m"

    colors = {
        'blue': blue,
        'red': red,
        'green': green,
        'yellow': yellow,
        'purple': purple,
        'cyan': cyan,
    }
    color = colors[color]

    def getDelimiter(a, b = None):
        if isString(b):
            if '\n' in b:
                return '\n'
            elif str(a).endswith(':'):
                return ''
        return ':'


    def baseChalk(s):
        if isCapitalized(s):
            return bold + color + str(s) + reset
        else:
            return color + str(s) + reset

    if mode == str:
        return baseChalk
    def promptChalk(a, b):
        delimiter = getDelimiter(a, b)
        print(bold + color + capitalize(a) + delimiter + reset, b)
        input()

    if mode == prompt:
        return promptChalk

    def chalk(*args, **kwargs):
        delimiter = getDelimiter(*args)
        if kwargs.get('bold') or isCapitalized(args[0]):
            print(bold + color + str(args[0]) + delimiter + reset, *args[1:])
        else:
            print(color + str(args[0]) + reset, *args[1:])

    return chalk

