def getParameters(s):
    m = search('\((.*?)\)', s)
    if m:
        parts = re.split(', *', m)
        args = []
        kwargs = []
        for part in parts:
            if test('\*\w', part):
                continue
            elif test('\w+ *=', part):
                kwargs.append(re.split(' *= *', part))
            else:
                args.append(part)
        return [args, kwargs]

def getParameters2(fn):
    import inspect
    signature = str(inspect.signature(fn))
    raw = signature[1:-1].split(', ')
    args = []
    kwargs = []
    for arg in raw:
        if '*' in arg:
            continue
        if '=' in arg:
            kwargs.append(arg.split('=')[0])
        else:
            args.append(arg)

    return [args, kwargs]

