        if is_function(key):
            if anti:
                return lambda x: not key(x)
            return key
        elif key.startswith(">"):
            limit = int(match(key, "> *(.*)"))
            return lambda x: x > limit
        elif key.startswith("<"):
            limit = int(match(key, "< *(.*)"))
