def smart_dedent(s):
    s = re.sub("^ *\n*|\n *$", "", s)
    if test(s, "^\S"):
        return s
    spaces = match(s, "^ *(?=\S)", flags=re.M)
    secondLineSpaces = match(s, "\n *(?=\S)")
    if (
        not spaces
        and secondLineSpaces
        and len(secondLineSpaces) > 4
    ):
        return re.sub(
            "^" + secondLineSpaces[5:], "", s, flags=re.M
        ).trim()

    return re.sub("^" + spaces, "", s, flags=re.M).strip()

