def checkpointf(**kwargs):

    exts = []
    r = None
    seconds = 0
    time = datetime.now().timestamp()

    ignore = [".git", "node_modules", "tests", "dist"]


    durations = [
        "minutes",
        "seconds",
        "hours",
        "weeks",
        "months",
        "days",
    ]

    before = kwargs.get("before", False)
    folders = 0 #default is files
    public = 0
    bigger = 0 # default is smaller
    size = 0
    empty = 0
    name = 0

    for k,v in kwargs.items():
        if not v:
            continue
        if k in commonExtensions:
            exts.append(k)
        elif k in durations:
            seconds += to_seconds(**dict({k: v}))
        elif k == "bigger":
            bigger = 1
        elif k == "empty":
            empty = 1

        elif k == "name":
            name = v

        elif k == "size":
            size = v
        elif k == "public":
            public = 1
        elif k == "private":
            public = 0
        elif k == "files":
            folders = 0
        elif k == "folders":
            folders = 1
        elif k == "r":
            r = v
        elif k == "image":
            exts.extend(["png", "jpg", "svg"])

    def checkpoint(file):
        filename = tail(file)
        if name and filename != name:
            return 0
        if exts and get_extension(file) not in exts:
            return 0
        if public and is_private(file):
            return 0
        if filename in ignore:
            return 0
        if folders:
            if is_file(file):
                return 0
            if empty and not is_empty_dir(file):
                return 0
        else:
            if not is_file(file):
                return 0

        if seconds:
            a = mdate(file) < time - seconds
            if before:
                if not a: return 0
            else:
                if a: return 0
        if size:
            if folders:
                pass
            else:
                if bigger:
                    if fsize(file) <= size:
                        return 0
                else:
                    if fsize(file) > size:
                        return 0
        if kwargs.get('no_duplicates') and test(filename, "\(\d+\)"):
            return 0
        if r and not test(filename, r):
            return 0
        return 1

    return checkpoint

