    if confirm:
        _confirm(command)

    process = Popen(command, stdout=PIPE, stderr=PIPE, shell=True)
    data = process.communicate()
    a, b = data
    success = trim(decode(a))
    error = trim(decode(b))
    if test(command, "^git", flags=re.M):
        pprint({"success": success, "error": error})
        if not "fatal" in error:
            error = False

    if mode == bool:
        if error:
            print("system command failed")
            return False
        return True
    if mode == str:
        if error:
            sys_exit(error)
        return success
    elif mode == dict:
        return {"success": success, "error": error}

def Dict(o):
    class _Dict:
        def __init__(self, **kwargs):
            for key, value in kwargs.items():
