def checkpointf(**kwargs):
    before = kwargs.get("before")
    after = kwargs.get("after")

    if before or after:
        hour = 0
        if before:
            hour = int(match(before, "\d+"))
        if after:
            hour = int(match(after, "\d+"))
        now = datetime.datetime.now()

        if now.hour < hour:
            now = now.replace(
                hour=hour, minute=0, second=0, microsecond=0
            ) - datetime.timedelta(days=1)
        else:
            now = now.replace(
                hour=hour, minute=0, second=0, microsecond=0
            )

        if before:
            checkpoints.append(lambda x: os.path.getmtime(x) < now)
        if after:
            checkpoints.append(lambda x: os.path.getmtime(x) > now)

    def checkpoint(file):
        for c in checkpoints:
            if c(file):
                continue
            else:
                return False
        return True

