def parse_structure(text: str) -> dict:
    structure = {}
    current_node = structure
    path_stack = []
    lines = text.strip().split("\n")

    for line in lines:
        if line.strip() == "":
            continue

        indent_level = len(line) - len(line.lstrip(" "))
        assert indent_level % 4 == 0
        item = line.strip()

        current_depth = indent_level // 4

        if current_depth > len(path_stack):
            path_stack.append(current_node)
        while current_depth < len(path_stack):
            path_stack.pop()
            current_node = path_stack[-1]

        if get_extension(item):
            current_node[item] = None
        else:
            current_node[item] = {}
            current_node = current_node[item]

    return structure

