        files = absdir(dir)
        for file in files:
            if is_file(file):
                if file_checkpoint(file):
                    store.append(file)
            elif is_dir(file):
                if dir_checkpoint(file) and file_checkpoint(file):
                    runner(file)

    if kwargs.get("kind") == "dir":

        def runner(dir):
            files = absdir(dir)
            for file in files:
