def maybe_fix_col(col, line):
    if line[col].isspace():
        # Scan left for the nearest non-whitespace character
        left = col
        while left > 0 and line[left].isspace():
            left -= 1
        # Scan right for the nearest non-whitespace character
        right = col
        while right < len(line) and line[right].isspace():
            right += 1
        # Adjust column to the closest non-whitespace side
        if col - left <= right - col and left > 0:
            col = left
        elif right < len(line):
            col = right
    return col

