def resolve_path(path):
    if not path:
        return
    s = """
        given a path like "./abc.js"
        it should resolve to the directory of the current file
        ~/2023/abc.js would be wrong
        because we might not be in the ~/2023 directory
    """

    cwd = get_dir()

    def resolver(x):
        nonlocal cwd
        count = len(x.group(0).split("/"))
        for i in range(count):
            cwd = head(cwd)
        return cwd + "/"

    if test(path, "^~/"):
        return expand(path)
    if test(path, "^\./[\w.-_]"):
        return os.path.join(cwd, path[2:])
    if test(path, "^\.?[a-z][\w.-_]+$", flags=re.I):
        return os.path.join(cwd, path)
    if test(path, "^/"):
        return path
    if test(path, "^\.\./"):
        return sub(path, "^(?:\.\./)+", resolver, count=1)

    # unreachable(f"what path could this be? it is not handled: {path}")

