def xfli(up, down, limit=100):
    max_line = get_linecount()
    start = get_lnum()
    a = start - 1

    c = 0
    state = LineState(a)
    while c < limit:
        if a == 0:
            break
        if up(state):
            break
        a -= 1

    c = 0
    state = LineState(b)
    while c < limit:
        if b == max_line:
            break
        if down(state):
            break
        b += 1
    return a, b

