def get_word_boundaries(back_re, forward_re=None):
    if not forward_re:
        forward_re = back_re
    s = get_line()
    max = len(s)
    count = 0
    c = get_col()
    a = c - 1
    b = c
    while True:
        count += 1
        # ($1)
        # ($1)
        # ($$1)
        # ($1)
        ch = s[a]
        if test(ch, back_re):
            if count == 1:
                max = len(s)
                if max - c >= c:
                    a += 2
                    b += 2
                else:
                    a -= 1
                    b -= 1
                continue
            break
        elif a == 0:
            break
        else:
            a -= 1

    while True:
        ch = s[b]
        if test(ch, forward_re):
            break
        else:
            b += 1
            if b == max:
                break

    return (c, a, b)

