def deep_equal(a, b):
    if a is b:
        return True

    if type(a) != type(b):
        return False
    if is_primitive(a) and is_primitive(b):
        return a == b

    if len(a) != len(b):
        return False

    if is_object(a):
        for key in a:
            if key not in b or not deep_equal(a[key], b[key]):
                return False
    else:  # Assuming 'a' is a collection that supports iteration by index
        for i in range(len(a)):
            if not deep_equal(a[i], b[i]):
                return False

    return True

def create_tests(fn, x, production=0):
    filetype = get_filetype()
    name = get_function_name(fn)
    create = False
    if is_string(x):
        r = "(^[a-z]+ [a-z]+.+)"
        items = split(x, r, flags=re.M)
        create = True

    def fix(e, args):
        return str(e)

    if create:

        def callback(desc, input):
            desc = trim(desc)
            input = to_argument(trim(input))
            output = attempt(fn, input, fix=fix)
            return {
                "input": input,
                "input_type": type(input).__name__,
                "output": output,
                "desc": desc,
            }

        data = map(partition(items), callback)
        if production:
            path = xpath(
                f"~/scratch/snapshots/{filetype}", name, "json"
            )
            merge_json(path, data)
            return data
        else:
            pprint(data)
    else:

        def callback(item):
            input = item["input"]
            output = attempt(fn, input, fix=fix)
            return {
                "input": input,
                "output": output,
                "expected": item["output"],
                "desc": item["desc"],
                "matches": deep_equal(output, item["output"]),
            }

        results = map(x, callback)
        success = every(results, lambda x: x["matches"] == True)
        return {
            "results": results,
            "success": success,
        }

