def str_object_format(items, mode):
    ref = jspy()
    delimiters = ref[mode + "_delimiters"]
    quotes = ref["object_quotes"]
    indent = " " * 4
    s = delimiters[0] + "\n"
    if mode == "array":
        items = unique(items)
        for item in items:
            s += f"{indent}{item},\n"
    else:
        for item in items:
            a, b = item if is_array(item) else (item, item)
            s += f"{indent}{quotes}{a}{quotes}: {b},\n"

    s += delimiters[1]
    return s

def xpath(*args):
