def to_timestamp(s=None, **kwargs):
    if is_string(s) and test(s, "="):
        a, b = findall(s, "\w+")
        kwargs[a] = b

    today = datetime.today()
    if kwargs:
        h = 0
        ref = {
            "days": 24,
            "hours": 1,
            "weeks": 168,
            "months": 672,
        }
        for k, v in kwargs.items():
            h += ref[k] * v
        return today - timedelta(hours=h)

    date_formats = [
        "%Y-%m-%d",  # ISO 8601 format
        "%Y/%m/%d",  # ISO 8601 format
        "%m/%d/%y",  # US date format with slashes
        "%m-%d-%y",  # US date format with dashes
        "%I%p",  # Time format like 9AM or 9PM
    ]

    for format in date_formats:
        try:
            if format == "%I%p":
                dt = datetime.combine(
                    today, datetime.strptime(s, format).time()
                )
            else:
                dt = datetime.strptime(s, format)
            return int(datetime.timestamp(dt))
        except ValueError:
            continue

    raise Exception("could not parse the date")

