def extract_frontmatter(s):
    s, m = mget(s, "^\n*---\n([\w\W]+?)\n---\n*")

    if not m:
        return s, {}
    fm = yaml.safe_load(m)

    for t in transformers:
        k = t.get("key")
        if k in fm:
            value = t.get("transform")(fm[k])
            if value != None:
                fm[k] = value

    return s, fm

