def grep(dir, pattern, boundary=False):
    excluded_directories = [
        "git",
        "node_modules",
    ]
    if boundary:
        pattern = "\\b" + pattern + "\\b"

    # -o prints just the matching part of the line
    cmd = f'grep -r -P --exclude-dir={{{",".join(excluded_directories)}}} "{pattern}" {dir}'
    result = system_command(cmd)
    s = result.get("success")
    if s:
        return split(s, "\n")

